// A schema, using GraphQL schema language
const schema = {
  Query: `#gql
      hello( myName: String ): String
  `
}

// Provides a resolver function for each API endpoint
var resolvers = {
  Query: {
    // eslint-disable-next-line
    hello: (_1,{myName},ctx,params) => `Hello, ${myName||'stranger'}!`
  }
}

export default {schema,resolvers}
