// A schema, using GraphQL schema language
var query = `
  hello( myName: String ): String
`
 
// Provides a resolver function for each API endpoint
var resolvers = {
  Query: {
    // eslint-disable-next-line
    hello: (_1,{myName},ctx,params) => {
      return `Hello, ${myName||'stranger'}!`
    },
  }
}

export default {query,resolvers}
